function beforeCancelProcess(colleagueId,processId){
	var ABERTURA = 0;
	var SOLICITAR = 4;
	var ELABORAR = 10
	var ASSINAR = 18;
	var ANEXAR = 57;
	
	var ativAtual  = getValue("WKNumState");	
	var idDocumento = getValue("WKCardId");
	
	if (ativAtual == ASSINAR ){
		 throw "Você não pode cancelar a solicitação desse contrato pois ela já está em fase de assinatura. " +
		 		"Procure o setor juridico para abordar o processo de assinatura digital." +
		 		"Caso o contrato já esteja assinado, será necessário realizar o distrato e " +
		 		"voltar para fase de cotação da contratação do serviço.";
			
	}
	
}

function setContrato(id,opcao){
	 var constraints = new Array();                                 
	 constraints.push(DatasetFactory.createConstraint("documentid", id, id, ConstraintType.MUST));
	 constraints.push(DatasetFactory.createConstraint("acao", opcao, opcao, ConstraintType.MUST));
    var resultDataset = DatasetFactory.getDataset("VM_CNTA300_SOLICITACAO_CONTRATO", null, constraints, null);                                                                    
   		     
        if (resultDataset.getValue(0,"RETORNO") != "SUCESSO"){
            throw resultDataset.getValue(0,"RETORNO");
        }
        else {
       	 	hAPI.setTaskComments(usuario, codSolicitacao, 0, "Contrato registrado automaticamente. Filial+Número+Revisão: " + filial+'-'+resultDataset.getValue(0,"NUMERO")+'-'+resultDataset.getValue(0,"REVISAO"));		        	 	
       	 	//GRAVA NUMERO DO CONTRATO NO FORMULARIO
				hAPI.setCardValue("Numerocontrato",resultDataset.getValue(0,"NUMERO"));
				hAPI.setCardValue("filial",filial);
				hAPI.setCardValue("revisao",resultDataset.getValue(0,"REVISAO"));
				
        }

}

function beforeStateEntry(sequenceId){
	var CONFIGURAR = 51;
	var ELABORAR = 10; 
	var ASSINAR = 18;
	var ANEXAR = 57;
	
	
	var ativAtual 		 = getValue("WKNumState");		
	var codSolicitacao 	 = getValue("WKNumProces");
	var nextAtv  		 = getValue("WKNextState");
	var idDocumento = getValue("WKCardId");
	var idFormulario = getValue("WKFormId")
	var empresa = getValue("WKCompany");
    var usuario = getValue('WKUser');
    
	var definicaoValor = hAPI.getCardValue("definicaoValor");	
	var contrato = hAPI.getCardValue("Numerocontrato");
	var revisao = hAPI.getCardValue("revisao");
	var statusContrato = hAPI.getCardValue("statusContrato");
	var tipoContrato = hAPI.getCardValue("tipoContrato");	   
	var tipoRevisao = hAPI.getCardValue("tipoRevisao");
	var filial = hAPI.getCardValue("filial");
	var cgc = hAPI.getCardValue("cnpjcpf");
	

		
	
	if (ativAtual == CONFIGURAR && nextAtv == ELABORAR){
		if ((tipoContrato == "" && tipoRevisao == "" ) || (tipoContrato == null && tipoRevisao == null ) ){
			throw "Tipo de contrato ou revisão não informado.";
		}
		else {
			setContrato(idDocumento,3); 
		}
		
	}
	else if (ativAtual == ELABORAR && nextAtv == CONFIGURAR){
			setContrato(idDocumento,5); 
			hAPI.setCardValue("Numerocontrato","");
			hAPI.setCardValue("filial","");
			hAPI.setCardValue("revisao","");
			
	}
	else if (ativAtual == ASSINAR){
	    	if (nextAtv == 37){
	    		//recusado pelo fornecedor - exclui o contrato no protheus
				if (statusContrato !="assinado"){				
					setContrato(idDocumento,5); 	
					hAPI.setCardValue("Numerocontrato","");
					hAPI.setCardValue("filial","");
					hAPI.setCardValue("revisao","");
					
				}
				//contrato assinado
				else {
					//chamar webservice que sincroniza contratos
					
				}
	    	}
    }
	else if (ativAtual ==  ANEXAR){
			if (nextAtv == CONFIGURAR){
				setContrato(idDocumento,5); 	
				hAPI.setCardValue("Numerocontrato","");
				hAPI.setCardValue("filial","");
				hAPI.setCardValue("revisao","");
			}
			else {
				//VERIFICA SE EXISTE ANEXO NO CONTRATO
				var lAnexo = getContratoAtivo(cgc,contrato);
				
				if (lAnexo == false){
					throw "É necessário que o contrato esteja vigente. Aguarde 30 minutos e tente novamente mais tarde.";
				}
			}
	
	
	
	}
    
    function setContrato(id,opcao){
			 var constraints = new Array();                                 
			 constraints.push(DatasetFactory.createConstraint("documentid", id, id, ConstraintType.MUST));
			 constraints.push(DatasetFactory.createConstraint("acao", opcao, opcao, ConstraintType.MUST));
		
			 constraints.push(DatasetFactory.createConstraint("contrato", contrato, contrato, ConstraintType.MUST));
			 constraints.push(DatasetFactory.createConstraint("tipo", tipoContrato, tipoContrato, ConstraintType.MUST));
			 constraints.push(DatasetFactory.createConstraint("revisao", revisao, revisao, ConstraintType.MUST));
			 constraints.push(DatasetFactory.createConstraint("tipoRevisao", tipoRevisao, tipoRevisao, ConstraintType.MUST));
			 //constraints.push(DatasetFactory.createConstraint("filial", filial, filial, ConstraintType.MUST));
			 
		     var resultDataset = DatasetFactory.getDataset("VM_CNTA300_SOLICITACAO_CONTRATO", null, constraints, null);                                                                    
		    		     
		         if (resultDataset.getValue(0,"RETORNO") != "SUCESSO"){
		             throw resultDataset.getValue(0,"RETORNO");
		         }
		         else {
		        	 
		        	 if (opcao != 5){
		        		//hAPI.setTaskComments(usuario, codSolicitacao, 0, "Contrato registrado automaticamente. Filial+Número+Revisão: " + filial+'-'+resultDataset.getValue(0,"NUMERO")+'-'+resultDataset.getValue(0,"REVISAO"));		        	 	
			        	 	//GRAVA NUMERO DO CONTRATO NO FORMULARIO
							hAPI.setCardValue("Numerocontrato",resultDataset.getValue(0,"NUMERO"));
							hAPI.setCardValue("filial",filial);
							hAPI.setCardValue("revisao",resultDataset.getValue(0,"REVISAO"));
		        	 }
		        	 	
						
		         }
    
    }
    //https://tdn.totvs.com/display/public/fluig/Guia+de+propriedades+dos+objetos#GuiadePropriedadesdosObjetos-DocumentDto
    //https://tdn.totvs.com/display/public/fluig/hAPI
    //https://tdn.totvs.com/display/public/fluig/Desenvolvimento+de+Eventos#DesenvolvimentodeEventos-DocumentDto
    //https://tdn.totvs.com/display/public/fluig/docAPI
    
    
    
    function getContratoAtivo(cgc,contrato){
    	var constraints = new Array();                                 
		constraints.push(DatasetFactory.createConstraint("CGC", cgc, cgc, ConstraintType.MUST)); 
    	var listaContratos = DatasetFactory.getDataset("ds_get_Contratos", null, constraints, null);                                                                    
	    		
	     for (var i =0; i < listaContratos.rowsCount ; i++  ){	    
	    	 if (listaContratos.getValue(i,"CONTRATO") == contrato){
	    		 return true;
	    	 }
	    	 
	     }
	  return false;
    }
    
    
	function convertDataToString(dataToString) {
	    var dia;

	    //MES INICIA DO ZERO POR ISSO SOMA 1 PARA ACHAR O MES CORRETO
	    var mes = dataToString.getMonth() + 1;
	    if (dataToString.getDate().toString().length == 1) {
	        dia = dataToString.getDate();
	        dia = "0" + dia.toString();

	    } else {
	        dia = dataToString.getDate();

	    }

	 
	    //converte mes
	    if (mes.toString().length == 1) {
	        mes = "0" + mes.toString();

	    }

	    //novo formato de data: para salvar em campos data do Fluig
	    return dia + "/" + mes + "/" + dataToString.getFullYear();


	}
    
    
	
}

function beforeTaskSave(colleagueId,nextSequenceId,userList){
	var ABERTURA = 0;
	var SOLICITAR = 4;
	var ELABORAR = 10
	var ASSINAR = 18;
	var ANEXAR = 57;
	
	//RECUPERA NUMERO DA ATIVIDADE
	var ativAtual  = getValue("WKNumState");	
	var solicitacaoPai;
	
	if (ativAtual == ABERTURA || ativAtual == SOLICITAR){
		var dataAtual = new Date();         
	    var dataSolicitacao = convertDataToString(dataAtual);
	         
		hAPI.setCardValue("dataSolicitacao", dataSolicitacao);
	
		//Recupera o numero da solicitação
	    var numProcess = getValue("WKNumProces");	    
		
		// Busca o número da solicitação pai
		var retornaProcessoSolicitacao = hAPI.getParentInstance(numProcess);
        
		if (retornaProcessoSolicitacao != 0 ){				
			// Busca os dados do formulário da solicitação pai
	        var parentCardData = hAPI.getCardData(retornaProcessoSolicitacao);
	        
	        // Replica um dado do formulário da solicitação pai para o formulário da solicitação filha
	        var solicitante = parentCardData.get("solicitante");
	        hAPI.setCardValue("responsavel", solicitante);	
	        
	 	        
	        
	        
	        
		}
		
	}

	function convertDataToString(dataToString) {
	    var dia;

	    //MES INICIA DO ZERO POR ISSO SOMA 1 PARA ACHAR O MES CORRETO
	    var mes = dataToString.getMonth() + 1;
	    if (dataToString.getDate().toString().length == 1) {
	        dia = dataToString.getDate();
	        dia = "0" + dia.toString();

	    } else {
	        dia = dataToString.getDate();

	    }

	    //converte mes
	    if (mes.toString().length == 1) {
	        mes = "0" + mes.toString();

	    }

	    //novo formato de data: para salvar em campos data do Fluig
	    return dia + "/" + mes + "/" + dataToString.getFullYear();


	}
	
}
